import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { Card, Image, Button, Spin, Result, Typography, message, InputNumber, Space } from 'antd';
import { ShoppingCartOutlined } from '@ant-design/icons';
import { Product } from '../types/product';
import http from '../utils/http';

const { Title, Text } = Typography;

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState<Product | null>(null);
  const [loading, setLoading] = useState(false);
  const [addingToCart, setAddingToCart] = useState(false);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      try {
        const { data } = await http.get(`/product/${id}`);
        setProduct(data.product);
      } catch (error) {
        console.error('获取商品详情失败:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleAddToCart = async () => {
    if (!product) return;
    setAddingToCart(true);
    try {
      await http.post('/cart/edit', {
        item: {
          product_id: product.id,
          quantity: quantity
        }
      });
      message.success('已添加到购物车');
    } catch (error) {
      console.error('添加到购物车失败:', error);
      message.error('添加到购物车失败，请重试');
    } finally {
      setAddingToCart(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <Spin size="large" />
      </div>
    );
  }

  if (!product) {
    return (
      <Result
        status="404"
        title="商品未找到"
        subTitle="抱歉，您访问的商品不存在"
      />
    );
  }

  return (
    <Card className="m-4">
      <div className="grid grid-cols-2 gap-8">
        <div>
          <Image
            src={product.picture}
            alt={product.product_name}
            className="w-full"
            fallback="data:image/png;base64,"
          />
        </div>
        <div>
          <Title level={2}>{product.product_name}</Title>
          <Text className="text-2xl text-red-600 block mt-4">
            ¥{product.price}
          </Text>
          <Text className="block mt-4">{product.description}</Text>
          <Space className="mt-6">
            <InputNumber
              min={1}
              max={99}
              value={quantity}
              onChange={(value) => setQuantity(value || 1)}
              addonBefore="数量"
            />
            <Button
              type="primary"
              icon={<ShoppingCartOutlined />}
              size="large"
              onClick={handleAddToCart}
              loading={addingToCart}
              danger
            >
              加入购物车
            </Button>
          </Space>
        </div>
      </div>
    </Card>
  );
};

export default ProductDetail;
